import mqtt, { MqttClient } from 'mqtt';
import { SensorData, MQTTConfig } from '../types';
import * as fileops from '../types/fileops_pb';
import { protobufService } from './protobufService';

class MQTTService {
  private client: MqttClient | null = null;
  private config: MQTTConfig | null = null;
  private sensorData: { [key: string]: SensorData } = {};
  private listeners: ((data: { [key: string]: SensorData }) => void)[] = [];
  private fileUploadResultListener: ((result: any) => void) | null = null;

  async connect(config: MQTTConfig): Promise<void> {
    return new Promise(async (resolve, reject) => {
      try {
        await protobufService.initialize();
        this.config = config;
        const url = `ws://${config.host}:8083`;
        this.client = mqtt.connect(url, {
          clientId: config.client_id,
          clean: true,
          connectTimeout: 4000,
          reconnectPeriod: 1000,
        });
        this.client.on('connect', () => {
          this.subscribeToTopics();
          resolve();
        });
        this.client.on('message', (topic, message) => {
          this.handleMessage(topic, message);
        });
        this.client.on('error', (error) => {
          reject(error);
        });
      } catch (error) {
        reject(error);
      }
    });
  }

  private subscribeToTopics(): void {
    if (!this.client || !this.config) return;
    Object.entries(this.config.topics).forEach(([key, topicConfig]) => {
      this.client!.subscribe(topicConfig.topic);
    });
  }

  private handleMessage(topic: string, message: Buffer): void {
    // Handle file upload result topic
    if (topic === 'uploads/success' && this.fileUploadResultListener) {
      try {
        const result = fileops.decodeFileUploadResult(message);
        this.fileUploadResultListener(result);
      } catch (e) {
        console.error('Failed to decode FileUploadResult:', e);
      }
      return;
    }
    // Handle sensor topics
    if (topic.startsWith('sensor/')) {
      console.log('Received topic:', topic);
      console.log('Raw message buffer:', message);
      const parsed = protobufService.parseMessage(topic, message);
      console.log('Parsed sensor data:', parsed);
      if (parsed) {
        const sensorKey = topic.split('/')[1];
        const formatted = protobufService.formatSensorData(topic, parsed);
        console.log('Formatted sensor data:', formatted);
        this.sensorData[sensorKey] = formatted;
        this.listeners.forEach((cb) => cb({ ...this.sensorData }));
      }
    }
  }

  // --- File Upload via MQTT ---
  async uploadFileViaMQTT(file: File, uploadDirectory: string, allowedExtensions: string[], publishTopic: string, returnTopic?: string): Promise<any> {
    if (!this.client) throw new Error('MQTT client not connected');
    const extension = file.name.substring(file.name.lastIndexOf('.'));
    if (!allowedExtensions.includes(extension)) {
      throw new Error('File extension not allowed');
    }
    const arrayBuffer = await file.arrayBuffer();
    const payload = fileops.encodeFileUploadRequest({
      filename: file.name,
      extension,
      file_data: new Uint8Array(arrayBuffer),
      upload_directory: uploadDirectory,
    });
    // Publish the file upload request
    this.client.publish(publishTopic, payload);
    // Optionally subscribe to return topic and wait for result
    if (returnTopic) {
      return new Promise((resolve, reject) => {
        this.fileUploadResultListener = (result) => {
          this.client!.unsubscribe(returnTopic);
          this.fileUploadResultListener = null;
          resolve(result);
        };
        this.client!.subscribe(returnTopic);
      });
    } else {
      return Promise.resolve();
    }
  }

  onSensorDataUpdate(callback: (data: { [key: string]: SensorData }) => void): void {
    this.listeners.push(callback);
  }

  getSensorData(): { [key: string]: SensorData } {
    return { ...this.sensorData };
  }

  disconnect(): void {
    if (this.client) {
      this.client.end();
      this.client = null;
    }
  }

  isConnected(): boolean {
    return this.client?.connected || false;
  }
}

export const mqttService = new MQTTService();
