import React, { useState, useEffect } from 'react';
import { Container, Nav, Navbar, Badge, Card, Row, Col, Button } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

import configData from './config/config.json';
import WsTest from './components/WsTest';
import SensorsTab from './components/SensorsTab';
import FilesTab from './components/FilesTab';

function App() {
  const [activeTab, setActiveTab] = useState('general');
  const [sensorData, setSensorData] = useState<{ [key: string]: any }>({});
  const [backendConnected, setBackendConnected] = useState(false);

  useEffect(() => {
    const ws = new WebSocket('ws://localhost:8000');
    ws.onopen = () => setBackendConnected(true);
    ws.onclose = () => setBackendConnected(false);
    ws.onerror = () => setBackendConnected(false);
    ws.onmessage = (event) => {
      const msg = JSON.parse(event.data);
      if (msg.type === 'sensor_update') {
        setSensorData(prev => ({ ...prev, [msg.sensor]: msg.data }));
      }
      if (msg.type === 'init') {
        setSensorData(msg.sensors || {});
      }
    };
    return () => ws.close();
  }, []);

  const handleButtonClick = (action: string) => {
    console.log(`Button clicked: ${action}`);
    // Here you would implement the actual button actions
    switch (action) {
      case 'refresh_all_sensors':
        console.log('Refreshing sensors...');
        break;
      case 'clear_sensor_history':
        console.log('Clearing sensor history...');
        break;
      case 'upload_file':
        console.log('Opening file upload...');
        break;
      case 'list_available_files':
        console.log('Listing files...');
        break;
      default:
        console.log(`Action not implemented: ${action}`);
    }
  };

  // Get enabled tabs from config
  const enabledTabs: [string, any][] = Object.entries(configData.tabs).filter(([_, tabConfig]) => tabConfig.enabled);

  return (
    <div className="App">
      {/*<WsTest />*/}
      <Navbar bg="primary" variant="dark" expand="lg" className="mb-3">
        <Container fluid>
          <Navbar.Brand>
            <i className="fas fa-server me-2"></i>
            {configData.application.name}
          </Navbar.Brand>
          <Navbar.Text className="ms-auto">
            <span style={{ display: 'inline-flex', alignItems: 'center', marginRight: 12 }}>
              <span
                style={{
                  display: 'inline-block',
                  width: 12,
                  height: 12,
                  borderRadius: '50%',
                  background: backendConnected ? '#00ff00' : '#ff0000',
                  marginRight: 6,
                  border: '1px solid #222',
                }}
                title={backendConnected ? 'Connected to backend' : 'Not connected to backend'}
              ></span>
              <span style={{ color: backendConnected ? '#00ff00' : '#ff0000', fontWeight: 600 }}>
                {backendConnected ? 'Backend Connected' : 'Backend Disconnected'}
              </span>
            </span>
            <Badge bg="success">
              <i className="fas fa-cog"></i> System
            </Badge>
          </Navbar.Text>
        </Container>
      </Navbar>

      <Container fluid>
        <Nav variant="tabs" className="mb-3">
          {enabledTabs.map(([tabId, tabConfig]) => (
            <Nav.Item key={tabId}>
              <Nav.Link
                active={activeTab === tabId}
                onClick={() => setActiveTab(tabId)}
                className="d-flex align-items-center"
              >
                <i className={`fas fa-${
                  tabId === 'general' ? 'info-circle' :
                  tabId === 'sensors' ? 'thermometer-half' :
                  tabId === 'video' ? 'video' : 'folder'
                } me-2`}></i>
                {tabConfig.title}
              </Nav.Link>
            </Nav.Item>
          ))}
        </Nav>

        <div className="tab-content">
          {activeTab === 'general' && (
            <Card>
              <Card.Header>
                <h5>
                  <i className="fas fa-info-circle me-2"></i>
                  {configData.tabs.general.title}
                </h5>
              </Card.Header>
              <Card.Body>
                <p className="text-muted">{configData.tabs.general.description}</p>
                <Row>
                  <Col md={6} lg={4} className="mb-3">
                    <div className="info-card p-3 border rounded">
                      <h6 className="text-muted mb-2">Machine Version</h6>
                      <div className="value h5 mb-2">Linux 6.8.0-60-generic</div>
                      <div className="timestamp small text-muted">Just now</div>
                    </div>
                  </Col>
                  <Col md={6} lg={4} className="mb-3">
                    <div className="info-card p-3 border rounded">
                      <h6 className="text-muted mb-2">Application Version</h6>
                      <div className="value h5 mb-2">{configData.application.version}</div>
                      <div className="timestamp small text-muted">Just now</div>
                    </div>
                  </Col>
                  <Col md={6} lg={4} className="mb-3">
                    <div className="info-card p-3 border rounded">
                      <h6 className="text-muted mb-2">CPU Usage</h6>
                      <div className="value h5 mb-2">15.2%</div>
                      <div className="timestamp small text-muted">Just now</div>
                    </div>
                  </Col>
                </Row>
              </Card.Body>
            </Card>
          )}

          {activeTab === 'sensors' && (
            <SensorsTab config={configData.tabs.sensors} sensorData={sensorData} backendConnected={backendConnected} />
          )}

          {activeTab === 'files' && (
            <FilesTab config={configData.tabs.files} />
          )}
        </div>
      </Container>
    </div>
  );
}

export default App;
